changeSet.sql:
set @concept_id = 0;
set @child1_concept_id = 0;
set @child2_concept_id = 0;
set @child3_concept_id = 0;
set @child4_concept_id = 0;

select concept_id into @concept_id from concept_name where name = "Provider name for Enhanced adherence counselling" and concept_name_type = 'FULLY_SPECIFIED' and locale = 'en' and voided = 0;

select concept_id into @child1_concept_id from concept_name where name = 'Bridget Muyotcha' and concept_name_type = 'FULLY_SPECIFIED' and locale = 'en' and voided = 0;
select concept_id into @child2_concept_id from concept_name where name = 'Viola Mudhe' and concept_name_type = 'FULLY_SPECIFIED' and locale = 'en' and voided = 0;
select concept_id into @child3_concept_id from concept_name where name = 'Judith Chabvuta' and concept_name_type = 'FULLY_SPECIFIED' and locale = 'en' and voided = 0;
select concept_id into @child4_concept_id from concept_name where name = 'Daphine Lingola' and concept_name_type = 'FULLY_SPECIFIED' and locale = 'en' and voided = 0;


call add_concept_answer(@concept_id, @child1_concept_id, 1);
call add_concept_answer(@concept_id, @child2_concept_id, 2);
call add_concept_answer(@concept_id, @child3_concept_id, 3);
call add_concept_answer(@concept_id, @child4_concept_id, 4);
