UPDATE concept_set SET sort_weight = 0
WHERE concept_set = (select concept_id from concept_view where concept_full_name = 'FPS, FP Counselling Only Form' and retired = 0)
AND concept_id = (select concept_id from concept_view where concept_full_name = 'FP Counselling Only, Service Provided' and retired = 0);

UPDATE concept_set SET sort_weight = 1
WHERE concept_set = (select concept_id from concept_view where concept_full_name = 'FPS, FP Counselling Only Form' and retired = 0)
AND concept_id = (select concept_id from concept_view where concept_full_name = 'FP Counselling Only, Weight' and retired = 0);

UPDATE concept_set SET sort_weight = 2
WHERE concept_set = (select concept_id from concept_view where concept_full_name = 'FPS, FP Counselling Only Form' and retired = 0)
AND concept_id = (select concept_id from concept_view where concept_full_name = 'FP Couns Form, Temperature' and retired = 0);

UPDATE concept_set SET sort_weight = 3
WHERE concept_set = (select concept_id from concept_view where concept_full_name = 'FPS, FP Counselling Only Form' and retired = 0)
AND concept_id = (select concept_id from concept_view where concept_full_name = 'FPS Counselling Only BP, Blood Pressure' and retired = 0);

UPDATE concept_set SET sort_weight = 4
WHERE concept_set = (select concept_id from concept_view where concept_full_name = 'FPS, FP Counselling Only Form' and retired = 0)
AND concept_id = (select concept_id from concept_view where concept_full_name = 'FP Counselling Only, Current Method' and retired = 0);

UPDATE concept_set SET sort_weight = 5
WHERE concept_set = (select concept_id from concept_view where concept_full_name = 'FPS, FP Counselling Only Form' and retired = 0)
AND concept_id = (select concept_id from concept_view where concept_full_name = 'FPS Counselling, IF Current method others, then specify' and retired = 0);

UPDATE concept_set SET sort_weight = 6
WHERE concept_set = (select concept_id from concept_view where concept_full_name = 'FPS, FP Counselling Only Form' and retired = 0)
AND concept_id = (select concept_id from concept_view where concept_full_name = 'FP Counselling Only, Assessment' and retired = 0);

UPDATE concept_set SET sort_weight = 7
WHERE concept_set = (select concept_id from concept_view where concept_full_name = 'FPS, FP Counselling Only Form' and retired = 0)
AND concept_id = (select concept_id from concept_view where concept_full_name = 'FP Counselling Only, Plan' and retired = 0);

UPDATE concept_set SET sort_weight = 8
WHERE concept_set = (select concept_id from concept_view where concept_full_name = 'FPS, FP Counselling Only Form' and retired = 0)
AND concept_id = (select concept_id from concept_view where concept_full_name = 'FP Counselling Only,Device Removal Section' and retired = 0);

UPDATE concept_set SET sort_weight = 9
WHERE concept_set = (select concept_id from concept_view where concept_full_name = 'FPS, FP Counselling Only Form' and retired = 0)
AND concept_id = (select concept_id from concept_view where concept_full_name = 'FP Counselling Only, Treatment given' and retired = 0);

UPDATE concept_set SET sort_weight = 10
WHERE concept_set = (select concept_id from concept_view where concept_full_name = 'FPS, FP Counselling Only Form' and retired = 0)
AND concept_id = (select concept_id from concept_view where concept_full_name = 'FPS Counselling Only, Referrals Section' and retired = 0);

UPDATE concept_set SET sort_weight = 11
WHERE concept_set = (select concept_id from concept_view where concept_full_name = 'FPS, FP Counselling Only Form' and retired = 0)
