WHERE concept_set = (select concept_id from concept_view where concept_full_name = 'STI Symptoms, Female Details' and retired = 0)
AND concept_id = (select concept_id from concept_view where concept_full_name = 'STI Symptoms, Female, Have you been treated for any of the following?' and retired = 0);
